import re
import json
from typing import List, Dict


PROJECT_START_RE = re.compile(
    r"\[([A-Za-z0-9\s\-&\.]+)\]\(https://housing\.com/in/buy/projects/page/",
    re.MULTILINE
)

PRICE_RE = re.compile(r"‚Çπ[\d\.]+\s*(?:L|Cr)(?:\s*-\s*‚Çπ[\d\.]+\s*(?:L|Cr))?")
BHK_RE = re.compile(r"\b[1-9](?:\.5)?\s*BHK\b")
POSSESSION_RE = re.compile(r"Possession:\s*([A-Za-z]+,\s*\d{4})")
AVG_PRICE_RE = re.compile(r"Avg\. Price:\s*‚Çπ([\d\.]+\s*K(?:\s*-\s*[\d\.]+\s*K)?)/sq\.ft")


def clean_text(text: str) -> str:
    text = re.sub(r"!\[.*?\]\(.*?\)", "", text)  # images
    text = re.sub(r"\[.*?\]\(https?://.*?\)", "", text)  # links
    text = re.sub(r"\s{2,}", " ", text)
    return text.strip()


def extract_projects(raw_text: str) -> List[Dict]:
    projects = []
    matches = list(PROJECT_START_RE.finditer(raw_text))

    for i, match in enumerate(matches):
        start = match.start()
        end = matches[i + 1].start() if i + 1 < len(matches) else len(raw_text)
        block = raw_text[start:end]

        name = match.group(1).strip()

        price = PRICE_RE.findall(block)
        bhk = sorted(set(BHK_RE.findall(block)))
        possession = POSSESSION_RE.search(block)
        avg_price = AVG_PRICE_RE.search(block)

        projects.append({
            "project_name": name,
            "bhk_types": bhk,
            "price_range": price[0] if price else None,
            "avg_price_per_sqft": avg_price.group(1) if avg_price else None,
            "possession": possession.group(1) if possession else None
        })

    return projects


def prepare_llm_payload(raw_text: str) -> Dict:
    cleaned = clean_text(raw_text)
    projects = extract_projects(cleaned)

    return {
        "city": "Pune",
        "locality": "Baner",
        "total_projects": len(projects),
        "projects": projects[:5]  # limit for LLM context
    }


